import { useState } from 'react'

const Display = (props) => {
  return (
    <div>
      {props.counter}
    </div>
  )
}

const Button = (props) => {
  return (
    <button onClick={props.onClicks}>
      {props.text}
    </button>
  )
}

const App = () => {
  const [counter, setCounter] = useState(0)
  const increaseByOne = () => setCounter(counter + 1)
  const decreaseByOne = () => setCounter(counter - 1)
  const setToZero = () => setCounter(0)


  return (
    <div>
      <Display counter={counter} />
      <Button
        onClicks={increaseByOne}
        text="plus"
      />
      <Button
        onClicks={setToZero}
        text="zero"
      />
      <Button
        onClick={decreaseByOne}
        text="minus"
      />

    </div>
  )

}

export default App
